import React, { Component } from "react";
import { List, Tag, Divider, Table, message,Popconfirm, Button ,Form,Input} from "antd";
import "./EditNotice.less";
import ApplyDataBase from '../ApplyDataBase'
import http from "../../../utils/api";
const noticeId = "620475440053";

class EditNotice extends React.Component {
  state = {
    data: [],
    record:{},
    page:'listPage'
  };
  componentDidMount = async () => {
    await this.getData();
  };
  getData = async () => {
    let res;
    try {
      res = await http().getTable({
        resid: noticeId
      });
      // if(res.data.error === 0){
      this.setState({
        data: res.data.data
      });
      console.log("re", res.data.data);
      // }
    } catch (error) {}
  };
  onApply= () => {
    this.setState({
      page:'applyPage'
    })
  }
  onBack = () => {
    this.setState({
      page:'listPage'
    })
  }
  onCheck = () => {
    this.setState({
      page:'checkPage'
    })
  }
  render() {
    const { data,record ,page} = this.state;
    const { getFieldDecorator } = this.props.form;
    const formItemLayout = {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 8 }
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 16 }
      }
    };
    const columns = [
      {
        title: "序号",
        dataIndex: "name",
        key: "name"
      },
      {
        title: "标题",
        dataIndex: "title",
        key: "title",
        render: text => <a>{text}</a>
      },
      {
        title: "消息内容",
        dataIndex: "content",
        key: "content"
      },
      {
        title: "发布者",
        dataIndex: "doctor",
        key: "doctor"
      },
      {
        title: "文件",
        dataIndex: "file",
        key: "file"
      },
      {
        title: "状态",
        dataIndex: "status",
        key: "status"
      },
      {
        title: "操作",
        key: "action",
        render: (text, record) => (
          <span>
            <a onClick={this.onCheck}>查看</a>
            <Divider type="vertical" />
            <a>修改</a>
            <Divider type="vertical" />
            <a>发布</a>
          </span>
        )
      }
    ];
    const pages = {
      checkPage: (
        <Form
          {...formItemLayout}
          // onSubmit={this.handleSubmit}
          className="applyDataBase-form"
        >
          <h1>参与CHASE-IBD数据库建设申请表</h1>
          <h3>申请医疗单位</h3>
          <Form.Item label={<span>申请人&nbsp;</span>}>
            <span>{record.doctor}</span>
          </Form.Item>
          <Form.Item label={<span>所属单位&nbsp;</span>}>
            <span>{record.hospital}</span>
          </Form.Item>
          <Form.Item label={<span>职称&nbsp;</span>}>
            <span>{record.post}</span>
          </Form.Item>
          <Form.Item label={<span>联系电话&nbsp;</span>}>
            <span>{record.phoneNumber}</span>
          </Form.Item>
          <Form.Item label={<span>E-mail&nbsp;</span>}>
            <span>{record.email}</span>
          </Form.Item>
          <Form.Item label={<span>IBD治疗团队名单&nbsp;</span>}>
            <span>{record.teamPeople}</span>&nbsp;&nbsp;
            <span>{record.teamPeople2}</span>&nbsp;&nbsp;
            <span>{record.teamPeople3}</span>&nbsp;&nbsp;
          </Form.Item>

          <h3>贵院目前患者数量</h3>

          <Form.Item label={<span>CD患者数量&nbsp;</span>}>
            <span>{record.CDPatientNumber}</span>
          </Form.Item>
          <Form.Item label={<span>UC患者数量&nbsp;</span>}>
            <span>{record.UCPatientNumber}</span>
          </Form.Item>
          <Form.Item label={<span>医师执照&nbsp;</span>}>
            <img
              src={record.doctorPhoto}
              alt="avatar"
              style={{ backgroundSize: "100%" }}
            />
          </Form.Item>

          <Button
            icon="download"
            type="primary"
            className="applyDataBase-form-print"
          >
            打印
          </Button>
        </Form>
      ),
      applyPage:(
          <Form
            {...formItemLayout}
            // onSubmit={this.handleSubmit}
            className="applyDataBase-form"
          >
            <h1>参与CHASE-IBD数据库建设申请表</h1>
            <Form.Item label={<span>标题&nbsp;</span>}>
              {getFieldDecorator("title", {
                rules: [
                  {
                    required: true,
                    message: "请输入标题",
                    whitespace: true
                  }
                ]
              })(<Input />)}
            </Form.Item>
            <Form.Item label={<span>所属单位&nbsp;</span>}>
              {getFieldDecorator("hospital", {
                rules: [
                  {
                    required: true,
                    message: "请输入所属单位",
                    whitespace: true
                  }
                ]
              })(<Input />)}
            </Form.Item>
            <Form.Item label={<span>职称&nbsp;</span>}>
              {getFieldDecorator("post", {
                rules: [
                  {
                    required: true,
                    message: "请输入您的职称！",
                    whitespace: true
                  }
                ]
              })(<Input />)}
            </Form.Item>
            <Form.Item label={<span>联系电话&nbsp;</span>}>
              {getFieldDecorator("phone", {
                rules: [
                  {
                    required: true,
                    message: "请输入您的联系电话!",
                    whitespace: true
                  }
                ]
              })(<Input />)}
            </Form.Item>
            <Form.Item label={<span>E-mail&nbsp;</span>}>
              {getFieldDecorator("email", {
                rules: [
                  {
                    required: true,
                    message: "请输入您的邮箱!",
                    whitespace: true
                  }
                ]
              })(<Input />)}
            </Form.Item>

            <Form.Item label={<span>CD患者数量：&nbsp;</span>}>
              {getFieldDecorator("CDNumber", {
                rules: [
                  {
                    required: true,
                    message: "请输入贵院的CD患者数量!",
                    whitespace: true
                  }
                ]
              })(<Input />)}
            </Form.Item>
            <Form.Item label={<span>UC患者数量：&nbsp;</span>}>
              {getFieldDecorator("UCNumber", {
                rules: [
                  {
                    required: true,
                    message: "请输入贵院的UC患者数量!",
                    whitespace: true
                  }
                ]
              })(<Input />)}
            </Form.Item>
            <Button
              className="applyDataBase-form-save"
              htmlType="submit"
              onClick={() => {
                this.handleSubmit(this, 1);
              }}
            >
              保存
            </Button>
            <Button
              type="primary"
              className="applyDataBase-form-submit"
              htmlType="submit"
              onClick={() => {
                this.handleSubmit(this, 2);
              }}
            >
              提交
