import React, { Component } from "react";
import {
  List,
  Tag,
  Divider,
  Table,
  message,
  Popconfirm,
  Button,
  Form,
  Input
} from "antd";
import "./EditNotice.less";
import ApplyDataBase from "../ApplyDataBase";
import http from "../../../utils/api";
import TextArea from "antd/lib/input/TextArea";
const noticeId = "620317621824";
const formItemLayout = {
  labelCol: {
    xs: { span: 24 },
    sm: { span: 8 }
  },
  wrapperCol: {
    xs: { span: 24 },
    sm: { span: 16 }
  }
};
const columns = props => {
  const { onCheck } = props;
  return [
    {
      title: "序号",
      dataIndex: "name",
      key: "name"
    },
    {
      title: "标题",
      dataIndex: "title",
      key: "title",
      render: text => <a>{text}</a>
    },
    {
      title: "消息内容",
      dataIndex: "content",
      key: "content"
    },
    {
      title: "发布者",
      dataIndex: "doctor",
      key: "doctor"
    },
    {
      title: "文件",
      dataIndex: "file",
      key: "file"
    },
    {
      title: "状态",
      dataIndex: "status",
      key: "status"
    },
    {
      title: "操作",
      key: "action",
      render: (text, record) => (
        <span>
          <a
            onClick={() => {
              onCheck();
            }}
          >
            查看
          </a>
          <Divider type="vertical" />
          <a>修改</a>
          <Divider type="vertical" />
          <a>发布</a>
        </span>
      )
    }
  ];
};

class EditNotice extends React.Component {
  state = {
    data: [],
    record: {},
    page: "listPage"
  };
  save = async e => {
    this.props.form.validateFieldsAndScroll((err, values) => {
     if(!err){
       this.onSubmit(values)
     }
  })
}
  onSubmit = async values => {
    let res;
    console.log("values",values)
     let data = [
      values
     ];
    try {
      res = await http().addRecords({
        resid: noticeId,
        data
      });
      this.setState({
        page: "listPage"
      });
    } catch (error) {}

    }

  publish = () => {
    this.setState({
      page: "listPage"
    });
  };
  renderContent = () => {
    let page = this.state.page;
    switch (page) {
      case "listPage":
        return this.renderListPage();
      case "addPage":
        return this.renderAddPage();
      case "checkPage":
        return this.renderCheckPage();
    }
  };
  renderListPage = () => {
    let data = this.state.data;
    return (
      <div className="editNotice">
        <Button type="primary" className="editNotice-add" onClick={this.onAdd}>
          添加新通知
        </Button>
        <Table
          className="EditNotice"
          columns={columns({ onCheck: this.onCheck })}
          dataSource={data}
        />
      </div>
    );
  };
  renderCheckPage = () => {
    const { getFieldDecorator } = this.props.form;
    let record = this.state.record;
    return (
      <div className="editNotice">
        <Button type="primary" className="editNotice-add" onClick={this.onBack}>
          返回
        </Button>
        <Form
          {...formItemLayout}
          // onSubmit={this.handleSubmit}
          className="editNotice-form"
        >
          <h1>消息编辑</h1>
          <Form.Item label={<span>消息标题&nbsp;</span>}>
            <span>{record.doctor}</span>
          </Form.Item>
          <Form.Item label={<span>消息内容&nbsp;</span>}>
            <span>{record.hospital}</span>
          </Form.Item>
          <Form.Item label={<span>文件&nbsp;</span>}>
            <span>{record.post}</span>
          </Form.Item>
          <Form.Item label={<span>医师执照&nbsp;</span>}>
            <img
              src={record.doctorPhoto}
              alt="avatar"
              style={{ backgroundSize: "100%" }}
            />
          </Form.Item>

          <Button
            icon="download"
            type="primary"
            className="applyDataBase-form-print"
          >
            打印
          </Button>
        </Form>
      </div>
    );
  };
  renderAddPage = () => {
    const { getFieldDecorator } = this.props.form;
    return (
      <div className="editNotice">
        <Button type="primary" className="editNotice-add" onClick={this.onBack}>
          返回
        </Button>
        <Form
          {...formItemLayout}
          // onSubmit={this.handleSubmit}
          className="editNotice-form"
        >
          <h1>消息编辑</h1>
          <Form.Item
            className="editNotice-form-item"
            label={<span>标题&nbsp;</span>}
          >
            {getFieldDecorator("title", {
              rules: [
                {
                  required: true,
                  message: "请输入标题",
                  whitespace: true
                }
              ]
            })(<Input />)}
          </Form.Item>
          <Form.Item
            className="editNotice-form-item"
            label={<span>消息内容&nbsp;</span>}
          >
            {getFieldDecorator("content", {
              rules: [
                {
                  required: true,
                  message: "请输入消息内容",
                  whitespace: true
                }
              ]
            })(<TextArea className="editNotice-form-item-textarea" />)}
          </Form.Item>
          <div className="editNotice-form-btns">
            <Button type="primary" htmlType="submit" onClick={this.save}>
              保存
            </Button>
            <Button type="primary" htmlType="submit" onClick={this.publish}>
              发布
            </Button>
          </div>
        </Form>
      </div>
    );
  };
  componentDidMount = async () => {
    await this.getData();
  };
  getData = async () => {
    let res;
    try {
      res = await http().getTable({
        resid: noticeId
      });
      // if(res.data.error === 0){
      this.setState({
        data: res.data.data
      });
      console.log("re", res.data.data);
      // }
    } catch (error) {}
  };
  onAdd = () => {
    this.setState({
      page: "addPage"
    });
  };
  onBack = () => {
    this.setState({
      page: "listPage"
    });
  };
  onCheck = () => {
    console.log("aaa");
    this.setState({
      page: "checkPage"
    });
  };
  render() {
    const { data, record, page } = this.state;

    return this.renderContent();
  }
}
export default Form.create()(EditNotice);
