import React, { Component } from "react";
import "./ApplyDataBase.less";
import { Collapse, Icon, Alert, Empty, Button, Form } from "antd";
import http from "../../../utils/api";
import { create } from "istanbul-reports";

class ApplyDataBase extends Component {
  state = {
    collapsed: false,
  };
  componentDidMount = async () => {
    let res;
  };
  render() {

    return (
      <div className="applyDataBase">
      <Form onSubmit={this.handleSubmit} className="login-form-userName">
              <h1>登录</h1>
              <Form.Item>
                {getFieldDecorator("phone", {
                  rules: [{ required: true, message: "请输入你的手机号!" }]
                })(
                  <Input
                    size="large"
                    prefix={
                      <Icon type="phone" style={{ color: "rgba(0,0,0,.25)" }} />
                    }
                    placeholder="手机号"
                  />
                )}
              </Form.Item>
              <Form.Item className="login-form-valid">
                {getFieldDecorator("valid", {
                  rules: [{ required: true, message: "请输入你的验证码!" }]
                })(
                  <Input
                    size="large"
                    className="login-form-valid-input"
                    prefix={
                      <Icon type="lock" style={{ color: "rgba(0,0,0,.25)" }} />
                    }
                    placeholder="验证码 "
                  />
                )}
              </Form.Item>
              {disabled ? (
                <Button
                  className="login-form-valid-countdown"
                  size="large"
                  type="primary"
                  disabled
                >
                  {counts}
                </Button>
              ) : (
                <Button
                  className="login-form-valid-button"
                  size="large"
                  type="primary"
                  onClick={this.getVerCode}
                >
                  获取验证码
                </Button>
              )}
              <Button
                type="primary"
                htmlType="submit"
                className="login-form-button"
                onClick={this.login}
                size="large"
              >
                登录
              </Button>
              <div className="login-form-register">
                <a onClick={this.onRegister}>注册</a>
              </div>
            </Form>
      </div>
    );
  }
}

export default create(Form)(ApplyDataBase) ;
