import React, { Component } from "react";
import "./Login.less";
import http from "../../../utils/api";
import { Form, message, Icon, Input, Button, Checkbox } from "antd";

class Login extends Component {
  state = {
    collapsed: false,
    title: "后台管理系统"
  };

  componentDidMount = () => {};

  login = e => {
    e.preventDefault();
  };
  getVerCode = async () => {
    const {form } = this.props;
    console.log(" from.getFieldValue", this.props.form.getFieldValue("phone"))
    let res;
    try {
      res = await http().getVerCode({
        telephone: form.getFieldValue("phone")
      });
    } catch (error) {}
  };
  onRegister = () => {
    this.props.history.push({
      pathname: "/register"
    });
  };
  render() {
    const { title } = this.state;
    const { getFieldDecorator } = this.props.form;
    return (
      <div className="page">
        <div className="login-contain">
          <Form onSubmit={this.handleSubmit} className="login-form-userName">
            <h1>登录</h1>
            <Form.Item>
              {getFieldDecorator("phone", {
                rules: [{ required: true, message: "请输入你的手机号!" }]
              })(
                <Input
                  size="large"
                  prefix={
                    <Icon type="user" style={{ color: "rgba(0,0,0,.25)" }} />
                  }
                  placeholder="手机号"
                />
              )}
            </Form.Item>
            <Form.Item className="login-form-valid">
              {getFieldDecorator("valid", {
                rules: [{ required: true, message: "请输入你的验证码!" }]
              })(
                <React.Fragment>
                  <Input
                    size="large"
                    className="login-form-valid-input"
                    prefix={
                      <Icon type="lock" style={{ color: "rgba(0,0,0,.25)" }} />
                    }
                    placeholder="验证码 "
                  />
                  <Button
                    className="login-form-valid-button"
                    size="large"
                    type="primary"
                    onClick={this.getVerCode}
                  >
                    获取验证码
                  </Button>
                </React.Fragment>
              )}
            </Form.Item>
            <Button
              type="primary"
              htmlType="submit"
              className="login-form-button"
              onClick={this.login}
              size="large"
            >
              登录
            </Button>
            <div className="login-form-register">
              <a onClick={this.onRegister}>注册</a>
            </div>
          </Form>
        </div>
      </div>
    );
  }
}

export default Form.create()(Login);
