import React, { Component } from "react";
import "./NoticeList.less";
import { Collapse, Icon, Alert ,Empty,Button} from "antd";
import http from "../../../utils/api";
const { Panel } = Collapse;
const noticeId = '620317621824'

class NoticeList extends Component {
  state = {
    collapsed: false,
    imageUrl: "",
    userInfo: {},
    currentNotice: [],
    historyNotice: []
  };
  componentDidMount = async () => {
    let res;
    try {
      res = await http().getTable({
        resid:noticeId
      })
     const [current, ...history] = res.data.data;
      this.setState({
        currentNotice:current,
        historyNotice:history
      })
    } catch (error) {

    }
  };
  render() {
    const customPanelStyle = {
      background: "#f7f7f7",
      borderRadius: 4,
      marginBottom: 24,
      border: 0,
      overflow: "hidden"
    };
    const {currentNotice,historyNotice} = this.state;
    const text = `
    A dog is a type of domesticated animal.
    Known for its loyalty and faithfulness,
    it can be found as a welcome guest in many households across the world.
  `;
    return (
      <div className="noticeList">
        <h2>最新通知</h2>
        <Alert
          message={currentNotice.tile}
          description={currentNotice.content}
          type="info"
          showIcon
          closeText="Close"
        />
        <div className="noticeList-line"> </div>
        <h3>历史通知</h3>
        <Collapse
          bordered={false}
          defaultActiveKey={["1"]}
          expandIcon={({ isActive }) => (
            <Icon type="caret-right" rotate={isActive ? 90 : 0} />
          )}
        >
          {historyNotice.length>0?
          historyNotice.map((item) => {
             return  <Panel
               header={item.title}
               key="1"
               style={customPanelStyle}
             >
               <p>{item.content}</p>
             </Panel>
          }):
          <Empty
    image="https://gw.alipayobjects.com/mdn/miniapp_social/afts/img/A*pevERLJC9v0AAAAAAAAAAABjAQAAAQ/original"
    imageStyle={{
      height: 60,
    }}
    description={
      <span>
        Customize <a href="#API">Description</a>
      </span>
    }
  >
    <Button type="primary">Create Now</Button>
  </Empty>}

        </Collapse>
      </div>
    );
  }
}

export default NoticeList;
