import React, { Component } from "react";
import "./Login.less";
import http from "../../../utils/api";
import { Form, message, Icon, Input, Button, Checkbox } from "antd";

class Login extends Component {
  state = {
    disabled: false,
    counts:0
  };

  componentDidMount = () => {};

  login = async e => {
    const { form } = this.props;
    const loginData = {
      mobileno: form.getFieldValue("phone"), // 手机号
      validCode: form.getFieldValue("valid"), // 验证码
      loginMethod: "mobile",
      useCookie: false
    };
    e.preventDefault();
    let res;
    try {
      res = await http().login(loginData);
      message.success(res.message);
      this.props.history.push({
        pathname: "/home"
      });
    } catch (error) {
      message.error(error.message);
    }
  };
  countDown = () => {
    const counts = 60;
    this.setState({
      disabled:true
    }) 
    setTimeout(() => {
      if(counts>0){
        counts--;
      }else{
        this.setState({
          disabled:false
        }) 
      }
      this.setState({
        counts
      })
    },1000)
  }
  getVerCode = async () => {
    const { form } = this.props;
    let res;
    // try {
    //   res = await http().getVerCode({
    //     telephone: form.getFieldValue("phone")
    //   });
    //   console.log("aaas");
    // } catch (error) {
    //   console.log(error);
    // }
  };
  onRegister = () => {
    this.props.history.push({
      pathname: "/register"
    });
  };
  render() {
    const { disabled, counts } = this.state;
    const { getFieldDecorator } = this.props.form;
    return (
      <div className="page">
        <div className="login-contain">
          <Form onSubmit={this.handleSubmit} className="login-form-userName">
            <h1>登录</h1>
            <Form.Item>
              {getFieldDecorator("phone", {
                rules: [{ required: true, message: "请输入你的手机号!" }]
              })(
                <Input
                  size="large"
                  prefix={
                    <Icon type="phone" style={{ color: "rgba(0,0,0,.25)" }} />
                  }
                  placeholder="手机号"
                />
              )}
            </Form.Item>
            <Form.Item className="login-form-valid">
              {getFieldDecorator("valid", {
                rules: [{ required: true, message: "请输入你的验证码!" }]
              })(
                <Input
                  size="large"
                  className="login-form-valid-input"
                  prefix={
                    <Icon type="lock" style={{ color: "rgba(0,0,0,.25)" }} />
                  }
                  placeholder="验证码 "
                />
              )}
            </Form.Item>
            {disabled ? (
              <span> {counts} </span>
            ) : (
              <Button
                className="login-form-valid-button"
                size="large"
                type="primary"
                onClick={this.getVerCode}
              >
                获取验证码
              </Button>
            )}
            <Button
              type="primary"
              htmlType="submit"
              className="login-form-button"
              onClick={this.login}
              size="large"
            >
              登录
            </Button>
            <div className="login-form-register">
              <a onClick={this.onRegister}>注册</a>
            </div>
          </Form>
        </div>
      </div>
    );
  }
}

export default Form.create()(Login);
