import React, { Component } from "react";
import "./Register.less";
import { Form, message, Icon, Input, Button, Checkbox } from "antd";
import http from "../../../utils/api";

class Register extends Component {
  state = {
    collapsed: false,
    title: "后台管理系统"
  };

  componentDidMount = () => {};

  login = (e) => {
    e.preventDefault();

  };
  onLogin = () => {
    this.props.history.push({
      pathname:"/login"
    })
  }

  countDown = () => {
    let counts = 60;
    this.setState({
      disabled: true
    });
    let countdown = setInterval(() => {
      if (counts > 0) {
        counts--;
        this.setState({
          counts
        });
      } else {
        this.setState({
          disabled: false
        });
        clearInterval(countdown);
      }
    }, 1000);
  };
  getVerCode = async () => {
    this.countDown();
    const { form } = this.props;
    let res;
    try {
      res = await http().getVerCode({
        telephone: form.getFieldValue("phone")
      });
      console.log("aaas");
    } catch (error) {
      console.log(error);
    }
  };
  register = async() => {
    const {form} = this.props;
    let res;
    let registerData = {
      Handphone: form.phoneNumber, // 手机号
      userid: form.idNumber, //身份证号
      personid: form.idNumber,
      validCode: form.verifyCode, // 验证码
      nickname: form.nickName, // 昵称
      validresid: 616852937051,
      // unionid: this.unionid,
      // openid: this.openid
    }
    try {
      res = await http().register(
        registerData
      )
    } catch (error) {
      message.error(error.message)
    }
  }
  render() {
    const { title } = this.state;
    const { getFieldDecorator } = this.props.form;
    return (
      <div className="page">
        <div className="register-contain">
          <Form onSubmit={this.handleSubmit} className="login-form-userName">
            <h1>注册</h1>
            <Form.Item>
              {getFieldDecorator("doctor", {
                rules: [{ required: true, message: "请输入您的姓名!" }]
              })(
                <Input
                  prefix={
                    <Icon type="user" style={{ color: "rgba(0,0,0,.25)" }} />
                  }
                  placeholder="姓名"
                />
              )}
            </Form.Item>
            <Form.Item>
              {getFieldDecorator("hospital", {
                rules: [{ required: true, message: "请输入您所在的医院!" }]
              })(
                <Input
                  prefix={
                    <Icon type="user" style={{ color: "rgba(0,0,0,.25)" }} />
                  }
                  placeholder="医院"
                />
              )}
            </Form.Item>
            <Form.Item>
              {getFieldDecorator("post", {
                rules: [{ required: true, message: "请输入您的职称!" }]
              })(
                <Input
                  prefix={
                    <Icon type="user" style={{ color: "rgba(0,0,0,.25)" }} />
                  }
                  placeholder="职称"
                />
              )}
            </Form.Item>
            <Form.Item>
              {getFieldDecorator("postNumber", {
                rules: [{ required: true, message: "请输入您的执业证号码!" }]
              })(
                <Input
                  prefix={
                    <Icon type="user" style={{ color: "rgba(0,0,0,.25)" }} />
                  }
                  placeholder="执业证号码"
                />
              )}
            </Form.Item>
            <Form.Item>
              {getFieldDecorator("phone", {
                rules: [{ required: true, message: "请输入您的手机号!" }]
              })(
                <Input
                size='large'
                  prefix={
                    <Icon type="phone" style={{ color: "rgba(0,0,0,.25)" }} />
                  }
                  placeholder="手机号"
                />
              )}
            </Form.Item>

            <Form.Item className="login-form-valid">
              {getFieldDecorator("valid", {
                rules: [{ required: true, message: "请输入你的验证码!" }]
              })(
                  <Input
                    size="large"
                    className="login-form-valid-input"
                    prefix={
                      <Icon type="lock" style={{ color: "rgba(0,0,0,.25)" }} />
                    }
                    placeholder="验证码 "
                  />
              )}
            </Form.Item>
            {disabled ? (
              <Button
                className="login-form-valid-countdown"
                size="large"
                type="primary"
                disabled
              >
                {counts}
              </Button>
            ) : (
              <Button
                className="login-form-valid-button"
                size="large"
                type="primary"
                onClick={this.getVerCode}
              >
                获取验证码
              </Button>
            )}
            <Button
              type="primary"
              htmlType="submit"
              className="login-form-button"
              onClick={this.register}
              size='large'
            >
              注册
            </Button>
            <div className='login-form-register'>
              <a onClick={this.onLogin}>登录</a>
            </div>
          </Form>
        </div>
      </div>
    );
  }
}

export default Form.create()(Register);
