import React, { Component } from "react";
import "./Home.less";
import { Menu, Icon, Button, Tabs, Upload } from "antd";
import logo from "../../../assets/images/1556898894199.jpg";
const { SubMenu } = Menu;
const { TabPane } = Tabs;

class Home extends Component {
  state = {
    collapsed: false,
    imageUrl: ""
  };
  toggleCollapsed = () => {
    this.setState({
      collapsed: !this.state.collapsed
    });
  };
  componentDidMount = () => {};
   getBase64 = (img, callback) => {
    const reader = new FileReader();
    reader.addEventListener('load', () => callback(reader.result));
    reader.readAsDataURL(img);
  }
  handleChange = info => {
    if (info.file.status === "uploading") {
      this.setState({ loading: true });
      return;
    }
    if (info.file.status === "done") {
      // Get this url from response in real world.
      this.getBase64(info.file.originFileObj, imageUrl =>
        this.setState({
          imageUrl,
          loading: false
        })
      );
    }
  };
  render() {
    const uploadButton = (
      <div>
        {/* <Icon type={this.state.loading ? 'loading' : 'plus'} /> */}
        <div className="ant-upload-text">请上传照片</div>
      </div>
    );
    const { imageUrl } = this.state;
    return (
      <div className="home">
        <div className="home-left">
          <Menu
            defaultSelectedKeys={["1"]}
            defaultOpenKeys={["sub1"]}
            mode="inline"
            theme="dark"
            style={{ height: "100vh" }}
            inlineCollapsed={this.state.collapsed}
          >
            <div className="home-left-logo">
              <img className="home-left-logo-img" src={logo}></img>
            </div>
            <Menu.Item key="1">
              <Icon type="pie-chart" />
              <span>CHASE-IBD数据库</span>
            </Menu.Item>
            <Menu.Item key="2">
              <Icon type="desktop" />
              <span>数据库委员会</span>
            </Menu.Item>
          </Menu>
        </div>
        <div className="home-right">
          <div className="home-right-top">
            <div className="home-right-top-tabs">
              <Tabs defaultActiveKey="1" tabBarStyle={{ height: "90px",display:"flex",alignItems:"center",paddingLeft:"50px" }}>
                <TabPane tab="申请加入数据库" key="1">
                  Content of Tab Pane 1
                </TabPane>
                <TabPane tab="研究项目申请" key="2">
                  Content of Tab Pane 2
                </TabPane>
                <TabPane tab="申请流程说明" key="3">
                  Content of Tab Pane 3
                </TabPane>
                <TabPane tab="项目公示" key="3">
                  Content of Tab Pane 3
                </TabPane>
              </Tabs>
            </div>

            <div className="home-right-top-user">
              {/* <Upload
                name="avatar"
                listType="picture-card"
                className="avatar-uploader"
                showUploadList={false}
                action="https://www.mocky.io/v2/5cc8019d300000980a055e76"
                onChange={this.handleChange}
              >
                {imageUrl ? (
                  <img src={imageUrl} alt="avatar" style={{ width: "100%" }} />
                ) : (
                  uploadButton
                )}
              </Upload> */}
             <div> <Icon type="user" style={{fontSize:"40px"}} /></div>
                <div>XXXX教授</div>
            </div>
            <div></div>
          </div>
          <div className="home-right-content">
            <div></div>
          </div>
        </div>
      </div>
    );
  }
}

export default Home;
