    render(){
        const {data} = this.state;
        const columns = [
            {
              title: '序号',
              dataIndex: 'name',
              key: 'name',
            },
            {
              title: '标题',
              dataIndex: 'title',
              key: 'title',
              render: text => <a>{text}</a>,
            },
            {
              title: '消息内容',
              dataIndex: 'content',
              key: 'content',
            },
            {
              title: '发布者',
              dataIndex: 'doctor',
              key: 'doctor',
            },
            {
              title: '文件',
              dataIndex: 'file',
              key: 'file',
            },
            {
              title: '状态',
              dataIndex: 'status',
              key: 'status',
            },
            // {
            //   title: 'Tags',
