import React, { Component } from "react";
import { List, Tag, Divider, Table, message,Form,Button,Checkbox} from "antd";
import "./ApproveList.less";
import http from "../../../utils/api";
const applyProjectId = "620475440053";

class ApproveList extends React.Component {
  state = {
    data: [{}],
    record:{},
    page:'listPage'
  };
  componentDidMount = async () => {
    await this.getData();
  };
  getData = async () => {
    let res;
    try {
      res = await http().getTable({
        resid: applyProjectId
      });
      // if(res.data.error === 0){
      this.setState({
        data: res.data.data
      });
      console.log("re", res.data.data);
      // }
    } catch (error) {}
  };
  onCheck = () => {
    this.setState({
      page:'checkPage'
    })
  }

  render() {
    const { data,record,page } = this.state;
    const options = [
      { label: "单中心研究", value: "单中心研究" },
      { label: "多中心研究", value: "多中心研究" },
      { label: "第三方研究", value: "第三方研究" },
      { label: "所有研究", value: "所有研究" }
    ]; const formItemLayout = {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 8 }
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 16 }
      }
    };
    const checkPage = (
      <Form
        {...formItemLayout}
        // onSubmit={this.handleSubmit}
        className="applyDataBase-form"
      >
       {prop.page === "checkPage"  ?
         <Button
            icon={
              prop.page === "applyPage" || prop.page === "checkPage" ? "rollback" : "plus"
            }
            type="primary"
            onClick={
              prop.page === "applyPage" || prop.page === "checkPage"
                ? this.props.onBack
                : this.props.onApply
            }
            className="applyDataBase-btn"
          >
          </Button>:null}
        <h1>参与CHASE-IBD数据库建设申请表</h1>
        <h3>申请医疗单位</h3>
        <Form.Item label={<span>申请人&nbsp;</span>}>
          <span>{record.doctor}</span>
        </Form.Item>
        <Form.Item label={<span>所属单位&nbsp;</span>}>
          <span>{record.hospital}</span>
        </Form.Item>
        <Form.Item label={<span>职称&nbsp;</span>}>
          <span>{record.post}</span>
        </Form.Item>
        <Form.Item label={<span>联系电话&nbsp;</span>}>
          <span>{record.phoneNumber}</span>
        </Form.Item>
        <Form.Item label={<span>E-mail&nbsp;</span>}>
          <span>{record.email}</span>
        </Form.Item>
        <Form.Item label={<span>IBD治疗团队名单&nbsp;</span>}>
          <span>{record.teamPeople}</span>&nbsp;&nbsp;
          <span>{record.teamPeople2}</span>&nbsp;&nbsp;
          <span>{record.teamPeople3}</span>&nbsp;&nbsp;
        </Form.Item>
        <Form.Item label={<span>选择参与数据库研究&nbsp;</span>}>
          <Checkbox.Group
            options={options}
            defaultValue={record.studyType}
            onChange={this.onChangeCheckbox}
          />
        </Form.Item>
        <h3>贵院目前患者数量</h3>

        <Form.Item label={<span>CD患者数量&nbsp;</span>}>
          <span>{record.CDPatientNumber}</span>
        </Form.Item>
        <Form.Item label={<span>UC患者数量&nbsp;</span>}>
          <span>{record.UCPatientNumber}</span>
        </Form.Item>
        <Form.Item label={<span>医师执照&nbsp;</span>}>
          <img
            src={record.doctorPhoto}
            alt="avatar"
            style={{ backgroundSize: "100%" }}
          />
        </Form.Item>

        <Button
          icon="download"
          type="primary"
          className="applyDataBase-form-print"
        >
          打印
        </Button>
      </Form>
    );
    const columns = [
      {
        title: "序号",
        dataIndex: "name",
        key: "name"
      },
      // {
      //   title: 'Name',
      //   dataIndex: 'name',
      //   key: 'name',
      //   render: text => <a>{text}</a>,
      // },
      {
        title: "申请人",
        dataIndex: "doctor",
        key: "doctor"
      },
      {
        title: "申请时间",
        dataIndex: "applyTime",
        key: "applyTime"
      },
      {
        title: "所属单位",
        dataIndex: "hospital",
        key: "hospital"
      },
      {
        title: "研究类型",
        dataIndex: "studyType",
        key: "studyType"
      },
      {
        title: "审批状态",
        dataIndex: "approveStatus",
        key: "approveStatus"
      },
      // {
      //   title: 'Tags',
      //   key: 'tags',
      //   dataIndex: 'tags',
      //   render: tags => (
      //     <span>
      //       {tags.map(tag => {
      //         let color = tag.length > 5 ? 'geekblue' : 'green';
      //         if (tag === 'loser') {
      //           color = 'volcano';
      //         }
      //         return (
      //           <Tag color={color} key={tag}>
      //             {tag.toUpperCase()}
      //           </Tag>
      //         );
      //       })}
      //     </span>
      //   ),
      // },
      {
        title: "操作",
        key: "action",
        render: (text, record) => (
          <span>
            <a onClick={this.onCheck}>查看</a>
            <Divider type="vertical" />
            <a>建议</a>
          </span>
        )
      }
    ];
    return page === "listPage" ? (
      <div className="approveList">
        <Table
          rowKey="1"
          className="EditNotice"
          columns={columns}
          dataSource={data}
        />
      </div>
    ) : (
      checkPage
    );
  }
}
export default ApproveList;
