import React, { Component } from "react";
import { List, Tag, Divider, Table, message,Popconfirm, Button ,Form,Input} from "antd";
import "./EditNotice.less";
import ApplyDataBase from '../ApplyDataBase'
import http from "../../../utils/api";
import TextArea from "antd/lib/input/TextArea";
const noticeId = "620475440053";
const formItemLayout = {
  labelCol: {
    xs: { span: 24 },
    sm: { span: 8 }
  },
  wrapperCol: {
    xs: { span: 24 },
    sm: { span: 16 }
  }
};
const columns = [
  {
    title: "序号",
    dataIndex: "name",
    key: "name"
  },
  {
    title: "标题",
    dataIndex: "title",
    key: "title",
    render: text => <a>{text}</a>
  },
  {
    title: "消息内容",
    dataIndex: "content",
    key: "content"
  },
  {
    title: "发布者",
    dataIndex: "doctor",
    key: "doctor"
  },
  {
    title: "文件",
    dataIndex: "file",
    key: "file"
  },
  {
    title: "状态",
    dataIndex: "status",
    key: "status"
  },
  {
    title: "操作",
    key: "action",
    render: (text, record) => (
      <span>
        <a onClick={() => {
          this.onCheck()
        }}>查看</a>
        <Divider type="vertical" />
        <a>修改</a>
        <Divider type="vertical" />
        <a>发布</a>
      </span>
    )
  }
];
class EditNotice extends React.Component {
  state = {
    data: [],
    record:{},
    page:'listPage'
  };
  renderContent = () => {
    let page = this.state.page;
    switch(page){
      case 'listPage':
        return this.renderListPage()
        case 'applyPage':
          return this.renderAddPage();
          case 'checkPage':
            return this.renderCheckPage();
    }
  }
  renderListPage = () => {
    let data = this.state.data;
    return   <div className='editNotice'>
    <Button type='primary' className='editNotice-add'>添加新通知</Button>
    <Table className='EditNotice' columns={columns}  dataSource={data} />
    </div>
  }
  renderCheckPage = () => {
    const { getFieldDecorator } = this.props.form;
    let record = this.state.record;
    return  <Form
    {...formItemLayout}
    // onSubmit={this.handleSubmit}
    className="applyDataBase-form"
  >
    <h1>消息编辑</h1>
    <Form.Item label={<span>消息标题&nbsp;</span>}>
      <span>{record.doctor}</span>
    </Form.Item>
    <Form.Item label={<span>消息内容&nbsp;</span>}>
      <span>{record.hospital}</span>
    </Form.Item>
    <Form.Item label={<span>文件&nbsp;</span>}>
      <span>{record.post}</span>
    </Form.Item>
    <Form.Item label={<span>医师执照&nbsp;</span>}>
      <img
        src={record.doctorPhoto}
        alt="avatar"
        style={{ backgroundSize: "100%" }}
      />
    </Form.Item>

    <Button
      icon="download"
      type="primary"
      className="applyDataBase-form-print"
    >
      打印
    </Button>
  </Form>
  }
  renderAddPage = () => {
    const { getFieldDecorator } = this.props.form;
    return  <Form
    {...formItemLayout}
    // onSubmit={this.handleSubmit}
    className="applyDataBase-form"
  >
    <h1>消息编辑</h1>
    <Form.Item label={<span>标题&nbsp;</span>}>
      {getFieldDecorator("title", {
        rules: [
          {
            required: true,
            message: "请输入标题",
            whitespace: true
          }
        ]
      })(<Input />)}
    </Form.Item>
    <Form.Item label={<span>消息内容&nbsp;</span>}>
      {getFieldDecorator("content", {
        rules: [
          {
            required: true,
            message: "请输入消息内容",
            whitespace: true
          }
        ]
      })(<TextArea />)}
    </Form.Item>
    <Button
      className="applyDataBase-form-save"
      htmlType="submit"
      onClick={() => {
        this.handleSubmit(this, 1);
      }}
    >
      保存
    </Button>
    <Button
      type="primary"
      className="applyDataBase-form-submit"
      htmlType="submit"
      onClick={() => {
        this.handleSubmit(this, 2);
      }}
    >
      提交
    </Button>
  </Form>
  }
  componentDidMount = async () => {
    await this.getData();
  };
  getData = async () => {
    let res;
    try {
      res = await http().getTable({
        resid: noticeId
      });
      // if(res.data.error === 0){
      this.setState({
        data: res.data.data
      });
      console.log("re", res.data.data);
      // }
    } catch (error) {}
  };
  onApply= () => {
    this.setState({
      page:'applyPage'
    })
  }
  onBack = () => {
    this.setState({
      page:'listPage'
    })
  }
  onCheck = () => {
    this.setState({
      page:'checkPage'
    })
  }
  render() {
    const { data,record ,page} = this.state;

    return (
      this.renderContent()

    )
  }
}
export default Form.create()(EditNotice)
