import React, { Component } from "react";
import { List, Tag, Divider, Table, message, Button } from "antd";
import "./EditNotice.less";
import ApplyDataBase from '../ApplyDataBase'
import http from "../../../utils/api";
const noticeId = "620475440053";

class EditNotice extends React.Component {
  state = {
    data: [],
  };
  componentDidMount = async () => {
    await this.getData();
  };
  getData = async () => {
    let res;
    try {
      res = await http().getTable({
        resid: noticeId
      });
      // if(res.data.error === 0){
      this.setState({
        data: res.data.data
      });
      console.log("re", res.data.data);
      // }
    } catch (error) {}
  };

  render() {
    const { data } = this.state;
    const columns = [
      {
        title: "序号",
        dataIndex: "name",
        key: "name"
      },
      {
        title: "标题",
        dataIndex: "title",
        key: "title",
        render: text => <a>{text}</a>
      },
      {
        title: "消息内容",
        dataIndex: "content",
        key: "content"
      },
      {
        title: "发布者",
        dataIndex: "doctor",
        key: "doctor"
      },
      {
        title: "文件",
        dataIndex: "file",
        key: "file"
      },
      {
        title: "状态",
        dataIndex: "status",
        key: "status"
      },
      {
        title: "操作",
        key: "action",
        render: (text, record) => (
          <span>
            <a>查看</a>
            <Divider type="vertical" />
            <a>修改</a>
            <Divider type="vertical" />
            <a>发布</a>
          </span>
        )
      }
    ];
    const pages = {
      checkPage: (
        <Form
          {...formItemLayout}
          // onSubmit={this.handleSubmit}
          className="applyDataBase-form"
        >
          <h1>参与CHASE-IBD数据库建设申请表</h1>
          <h3>申请医疗单位</h3>
          <Form.Item label={<span>申请人&nbsp;</span>}>
            <span>{record.doctor}</span>
          </Form.Item>
          <Form.Item label={<span>所属单位&nbsp;</span>}>
            <span>{record.hospital}</span>
          </Form.Item>
          <Form.Item label={<span>职称&nbsp;</span>}>
            <span>{record.post}</span>
          </Form.Item>
          <Form.Item label={<span>联系电话&nbsp;</span>}>
            <span>{record.phoneNumber}</span>
          </Form.Item>
          <Form.Item label={<span>E-mail&nbsp;</span>}>
            <span>{record.email}</span>
          </Form.Item>
          <Form.Item label={<span>IBD治疗团队名单&nbsp;</span>}>
            <span>{record.teamPeople}</span>&nbsp;&nbsp;
            <span>{record.teamPeople2}</span>&nbsp;&nbsp;
            <span>{record.teamPeople3}</span>&nbsp;&nbsp;
          </Form.Item>
          <Form.Item label={<span>选择参与数据库研究&nbsp;</span>}>
            <Checkbox.Group
              options={options}
              defaultValue={record.studyType}
              onChange={this.onChangeCheckbox}
            />
          </Form.Item>
          <h3>贵院目前患者数量</h3>

          <Form.Item label={<span>CD患者数量&nbsp;</span>}>
            <span>{record.CDPatientNumber}</span>
          </Form.Item>
          <Form.Item label={<span>UC患者数量&nbsp;</span>}>
            <span>{record.UCPatientNumber}</span>
          </Form.Item>
          <Form.Item label={<span>医师执照&nbsp;</span>}>
            <img
              src={record.doctorPhoto}
              alt="avatar"
              style={{ backgroundSize: "100%" }}
            />
          </Form.Item>

          <Button
            icon="download"
            type="primary"
            className="applyDataBase-form-print"
          >
            打印
          </Button>
        </Form>
      ),
      listPage: (
        <List
          className="applyDataBase-content-list"
          itemLayout="horizontal"
          dataSource={applyList}
          renderItem={item => (
            <List.Item
              actions={[
                <a
                  key="list-loadmore-edit"
                  onClick={() => {
                    this.onCheck(item);
                  }}
                >
                  查看
                </a>,
                <Popconfirm
                  title="你确定要提交吗"
                  onConfirm={() => {
                    this.onSubmitRecord(item);
                  }}
                  okText="Yes"
                  cancelText="No"
                >
                  <a key="list-loadmore-edit">提交</a>
                </Popconfirm>
              ]}
            >
              {/* <Skeleton avatar title={false} loading={item.doctor} active> */}
              <List.Item.Meta
                // avatar={
                //   <Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />
                // }
                title={
                  <React.Fragment>
                    <a className="applyDataBase-content-list-word" href="#">
                      申请人:{item.doctor}
                    </a>
                    <a className="applyDataBase-content-list-word" href="#">
                      所属单位:{item.hospital}
                    </a>
                  </React.Fragment>
                }
                description="草稿"
              />
              <div>申请日期：{item.REC_CRTTIME}</div>
              {/* </Skeleton> */}
            </List.Item>
          )}
        />
      ),
    }
    return (
      <ApplyDataBase
        pages={pages}
        page={this.state.page}
        onBack={this.onBack}
        showImage={this.showImage}
        onApply={this.onApply}
      ></ApplyDataBase>

      // <div className='editNotice'>
      //   <Button type='primary' className='editNotice-add'>添加新通知</Button>
      //   <Table className='EditNotice' columns={columns}  dataSource={data} />
      //   </div>
    );
  }
}
export default EditNotice;
