import React, { Component } from "react";
import "./ApplyDataBase.less";
import {
  Collapse,
  Icon,
  Input,
  Upload,
  Button,
  Form,
  Checkbox,
  List,
  Skeleton,
  Avatar,
  message,
  Popconfirm
} from "antd";
import http from "../../../utils/api";
const applyDataBaseId = "620384838453";

class ApplyDataBase extends Component {
  state = {
    imageUrl: "",
    applyList: [],
    showApply: false,
    page: "listPage",
    record:{}
  };
  componentDidMount = async () => {
    let res;
    await this.getApplyData();
  };
  getApplyData = async () => {
    let res;
    try {
      res = await http().getTable({
        resid: applyDataBaseId
      });
      if (res.data.error == 0) {
        this.setState({
          applyList: res.data.data
        });
      }
    } catch (error) {
      message.error(error.message);
    }
  };
  onApply = () => {
    this.setState({
      page: "applyPage"
    });
  };
  onBack = () => {
    this.setState({
      page: "listPage"
    });
  };
  onCheck = (item) => {
    this.setState({
      page: "checkPage",
      record:item
    });
  };
  onChangeCheckbox = checkedValues => {

    let study = checkedValues.splice(1)
    console.log("checkedValues", checkedValues);
  };
  handleSubmit = (e, type) => {
    // console.log("e",e,type)
    // e.preventDefault();
    this.props.form.validateFieldsAndScroll((err, values) => {
      if (!err) {
        this.onSubmit(values, type);
      }
    });
  };
  onSubmitRecord = async record => {
    let res;
    record.isSubmit = "Y";
    try {
      res = await http().modifyRecords({
        resid: applyDataBaseId,
        data: record
      });
      if (res.data.error == 0) {
        message.success("提交成功");
      }
    } catch (error) {
      message.error(error.message);
    }
  };
  onSubmit = async (values, type) => {
    let res;
    let obj = {}
    let study = values.study.splice(1)
    console.log("a",study)
    if (type === 1) {

      obj = {
        doctor: values.doctor,
        hospital: values.hospital,
        post: values.post,
        phoneNumber: values.phone,
        email: values.email,
        teamPeople: values.teamPeople,
        teamPeople2: values.teamPeople2,
        teamPeople3: values.teamPeople3,
        studyType: values.study,
        CDPatientNumber: values.CDNumber,
        UCPatientNumber: values.UCNumber,
        doctorPhoto: values.doctorPhotos,
        studyType:values.study
      };
    } else {
      obj = {
        doctor: values.doctor,
        hospital: values.hospital,
        post: values.post,
        phoneNumber: values.phone,
        email: values.email,
        teamPeople: values.teamPeople,
        teamPeople2: values.teamPeople2,
        teamPeople3: values.teamPeople3,
        studyType: values.study,
        CDPatientNumber: values.CDNumber,
        UCPatientNumber: values.UCNumber,
        doctorPhoto: values.doctorPhotos,
        isSubmit: "Y"
      };
    }
    try {
      res = await http().addRecords({
        resid: applyDataBaseId,
        data:[obj]
      });
      if (res.data.Error == 0) {
        message.success("申请成功！");
      }
      this.setState({
        page: "listPage"
      });
      this.getApplyData();
    } catch (error) {
      message.error(error.message);
    }
  };
  renderPage = () => {
    let page = this.state.page;
    // switch(page){
    //   case 'applyPage':
    //     return (
    //       this.applyPage
    //     )
    //     case 'checkPage':
    //       return (
    //         applyPage
    //       )
    //       case 'listPage':
    //         return (
    //           listPage
    //         )
    // }
  };
  render() {
    const { getFieldDecorator } = this.props.form;
    const { imageUrl, applyList, showApply, page,record } = this.state;
    const formItemLayout = {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 8 }
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 16 }
      }
    };
    const tailFormItemLayout = {
      wrapperCol: {
        xs: {
          span: 24,
          offset: 0
        },
        sm: {
          span: 16,
          offset: 8
        }
      }
    };
    const uploadButton = (
      <div>
        <Icon type={this.state.loading ? "loading" : "plus"} />
        <div className="ant-upload-text">Upload</div>
      </div>
    );
    const options = [
      { label: "单中心研究", value: "单中心研究" },
      { label: "多中心研究", value: "多中心研究" },
      { label: "第三方研究", value: "第三方研究" },
      { label: "所有研究", value: "所有研究" }
    ];
    const checkPage = (
      <Form
        {...formItemLayout}
        // onSubmit={this.handleSubmit}
        className="applyDataBase-form"
      >
        <h1>参与CHASE-IBD数据库建设申请表</h1>
        <h3>申请医疗单位</h3>
        <Form.Item label={<span>申请人&nbsp;</span>}>
          <span>{record.doctor}</span>
        </Form.Item>
        <Form.Item label={<span>所属单位&nbsp;</span>}>
          <span>{record.hospital}</span>
        </Form.Item>
        <Form.Item label={<span>职称&nbsp;</span>}>
          <span>{record.post}</span>
        </Form.Item>
        <Form.Item label={<span>联系电话&nbsp;</span>}>
          <span>{record.phoneNumber}</span>
        </Form.Item>
        <Form.Item label={<span>E-mail&nbsp;</span>}>
          <span>{record.email}</span>
        </Form.Item>
        <Form.Item label={<span>IBD治疗团队名单&nbsp;</span>}>
          <span>{record.teamPeople}</span>
          <span>{record.teamPeople2}</span>
          <span>{record.teamPeople3}</span>
        </Form.Item>
        <Form.Item label={<span>选择参与数据库研究&nbsp;</span>}>
          {getFieldDecorator("study", {
            rules: []
          })(
            <Checkbox.Group
              options={options}
              onChange={this.onChangeCheckbox}
            />
          )}
        </Form.Item>
        <h3>贵院目前患者数量</h3>

        <Form.Item label={<span>CD患者数量&nbsp;</span>}>
          <span>{record.CDPatientNumber}</span>
        </Form.Item>
        <Form.Item label={<span>UC患者数量&nbsp;</span>}>
          <span>{record.UCPatientNumber}</span>
        </Form.Item>
        <Form.Item label={<span>医师执照&nbsp;</span>}>
          {getFieldDecorator("doctorPhoto", {})(
            <Upload
              name="avatar"
              listType="picture-card"
              className="avatar-uploader"
              showUploadList={false}
              action="https://www.mocky.io/v2/5cc8019d300000980a055e76"
              onChange={this.handleChange}
            >
              {imageUrl ? (
                <img src={imageUrl} alt="avatar" style={{ width: "100%" }} />
              ) : (
                uploadButton
              )}
            </Upload>
          )}
        </Form.Item>

        <Button
          icon="download"
          type="primary"
          className="applyDataBase-form-print"
        >
          打印
        </Button>
      </Form>
    );
    const listPage = (
      <List
        className="applyDataBase-content-list"
        itemLayout="horizontal"
        dataSource={applyList}
        renderItem={item => (
          <List.Item
            actions={[
              <a key="list-loadmore-edit" onClick={() => {
                this.onCheck(item)
              }}>
                查看
              </a>,
              <Popconfirm
                title="你确定要提交吗"
                onConfirm={() => {
                  this.onSubmitRecord(item);
                }}
                okText="Yes"
                cancelText="No"
              >
                {" "}
                <a key="list-loadmore-edit">提交</a>
              </Popconfirm>
            ]}
          >
            {/* <Skeleton avatar title={false} loading={item.doctor} active> */}
            <List.Item.Meta
              avatar={
                <Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />
              }
              title={
                <React.Fragment>
                  <a className="applyDataBase-content-list-word" href="#">
                    申请人:{item.doctor}
                  </a>
                  <a className="applyDataBase-content-list-word" href="#">
                    所属单位:{item.hospital}
                  </a>
                </React.Fragment>
              }
              description="草稿"
            />
            <div>申请日期：{item.REC_CRTTIME}</div>
            {/* </Skeleton> */}
          </List.Item>
        )}
      />
    );
    const applyPage = (
      <Form
        {...formItemLayout}
        // onSubmit={this.handleSubmit}
        className="applyDataBase-form"
      >
        <h1>参与CHASE-IBD数据库建设申请表</h1>
        <h3>申请医疗单位</h3>
        <Form.Item label={<span>申请人&nbsp;</span>}>
          {getFieldDecorator("doctor", {
            rules: [
              {
                required: true,
                message: "请输入申请人",
                whitespace: true
              }
            ]
          })(<Input />)}
        </Form.Item>
        <Form.Item label={<span>所属单位&nbsp;</span>}>
          {getFieldDecorator("hospital", {
            rules: [
              {
                required: true,
                message: "请输入所属单位",
                whitespace: true
              }
            ]
          })(<Input />)}
        </Form.Item>
        <Form.Item label={<span>职称&nbsp;</span>}>
          {getFieldDecorator("post", {
            rules: [
              {
                required: true,
                message: "请输入您的职称！",
                whitespace: true
              }
            ]
          })(<Input />)}
        </Form.Item>
        <Form.Item label={<span>联系电话&nbsp;</span>}>
          {getFieldDecorator("phone", {
            rules: [
              {
                required: true,
                message: "请输入您的联系电话!",
                whitespace: true
              }
            ]
          })(<Input />)}
        </Form.Item>
        <Form.Item label={<span>E-mail&nbsp;</span>}>
          {getFieldDecorator("email", {
            rules: [
              {
                required: true,
                message: "请输入您的邮箱!",
                whitespace: true
              }
            ]
          })(<Input />)}
        </Form.Item>
        <Form.Item label={<span>IBD治疗团队名单&nbsp;</span>}>
          {getFieldDecorator("teamPeople", {})(
            <React.Fragment>
              <Input />
            </React.Fragment>
          )}
          {getFieldDecorator("teamPeople2", {})(
            <React.Fragment>
              <Input />
            </React.Fragment>
          )}
          {getFieldDecorator("teamPeople3", {})(
            <React.Fragment>
              <Input />
            </React.Fragment>
          )}
        </Form.Item>
        <Form.Item label={<span>选择参与数据库研究&nbsp;</span>}>
          {getFieldDecorator("study", {
            rules: []
          })(
            <Checkbox.Group
              options={options}
              onChange={this.onChangeCheckbox}
            />
          )}
        </Form.Item>
        <h3>贵院目前患者数量</h3>

        <Form.Item label={<span>CD患者数量：&nbsp;</span>}>
          {getFieldDecorator("CDNumber", {
            rules: [
              {
                required: true,
                message: "请输入贵院的CD患者数量!",
                whitespace: true
              }
            ]
          })(<Input />)}
        </Form.Item>
        <Form.Item label={<span>UC患者数量：&nbsp;</span>}>
          {getFieldDecorator("UCNumber", {
            rules: [
              {
                required: true,
                message: "请输入贵院的UC患者数量!",
                whitespace: true
              }
            ]
          })(<Input />)}
        </Form.Item>
        <Form.Item label={<span>医师执照：&nbsp;</span>}>
          {getFieldDecorator("doctorPhoto", {})(
            <Upload
              name="avatar"
              listType="picture-card"
              className="avatar-uploader"
              showUploadList={false}
              action="https://www.mocky.io/v2/5cc8019d300000980a055e76"
              onChange={this.handleChange}
            >
              {imageUrl ? (
                <img src={imageUrl} alt="avatar" style={{ width: "100%" }} />
              ) : (
                uploadButton
              )}
            </Upload>
          )}
        </Form.Item>
        <Button
          className="applyDataBase-form-save"
          htmlType="submit"
          onClick={() => {
            this.handleSubmit(this, 1);
          }}
        >
          保存
        </Button>
        <Button
          type="primary"
          className="applyDataBase-form-submit"
          htmlType="submit"
          onClick={() => {
            this.handleSubmit(this, 2);
          }}
        >
          提交
        </Button>
      </Form>
    );

    return (
      <div className="applyDataBase">
        <div className="applyDataBase-btns">
          <Button
            icon={
              page === "applyPage" || page === "checkPage" ? "rollback" : "plus"
            }
            type="primary"
            onClick={
              page === "applyPage" || page === "checkPage"
                ? this.onBack
                : this.onApply
            }
            className="applyDataBase-btn"
          >
            {page === "applyPage" || page === "checkPage" ? "返回" : "申请"}
          </Button>
          <a className="applyDataBase-photoBtn" type="link">
            申请流程图
          </a>
        </div>
        <div className="applyDataBase-content">
          {page === "applyPage"
            ? applyPage
            : (this.state.page === "checkPage"
            ? checkPage
            : listPage)}
        </div>
      </div>
    );
  }
}

export default Form.create()(ApplyDataBase);
