import React, { Component } from "react";
import {
  List,
  Tag,
  Divider,
  Table,
  message,
  Popconfirm,
  Button,
  Form,
  Input,
  Spin,
  Modal
} from "antd";
import "./EditNotice.less";
import ApplyDataBase from "../ApplyDataBase";
import http from "../../../utils/api";
import TextArea from "antd/lib/input/TextArea";
const noticeId = "620317621824";
const formItemLayout = {
  labelCol: {
    xs: { span: 24 },
    sm: { span: 8 }
  },
  wrapperCol: {
    xs: { span: 24 },
    sm: { span: 16 }
  }
};
const columns = props => {
  const { onEdit } = props;
  return [
    {
      title: "序号",
      dataIndex: "name",
      key: "name"
    },
    {
      title: "标题",
      dataIndex: "title",
      key: "title",
      render: text => <a>{text}</a>
    },
    {
      title: "消息内容",
      dataIndex: "content",
      key: "content"
    },
    {
      title: "发布者",
      dataIndex: "doctor",
      key: "doctor"
    },
    {
      title: "文件",
      dataIndex: "file",
      key: "file"
    },
    {
      title: "状态",
      dataIndex: "status",
      key: "status"
    },
    {
      title: "操作",
      key: "action",
      render: (text, record) => (
        <span>
          <a
            onClick={() => {
              console.log("re", record);
              onEdit(record);
            }}
          >
            修改
          </a>
          <Divider type="vertical" />
          <a>发布</a>
        </span>
      )
    }
  ];
};

class EditNotice extends React.Component {
  state = {
    data: [],
    record: {},
    page: "listPage", //当前页面
    spin: false, //是否显示加载
    visible: false, //是否显示添加模态框
    editVisible: false //是否显示查看模态框
  };
  handleCancel = () => {
    this.setState({
      visible: false,
      editVisible: false
    });
  };
  save = async (e, type) => {
    this.props.form.validateFieldsAndScroll((err, values) => {
      if (!err) {
        this.onSubmit(values, type);
      }
    });
  };
  onSubmit = async (values, type) => {
    let res;
    if (type === 1) {
      let data = [values];
      try {
        res = await http().addRecords({
          resid: noticeId,
          data
        });
        this.setState({
          visible: false
        });
        await this.getData();
      } catch (error) {}
    } else {
      try {
        res = await http().modifyRecords({
          resid: noticeId,
          data
        });
        this.setState({
          visible: false
        });
        await this.getData();
      } catch (error) {}
    }
  };

  publish = () => {
    this.setState({
      page: "listPage"
    });
  };
  renderContent = () => {
    let page = this.state.page;
    switch (page) {
      case "listPage":
        return this.renderListPage();
      case "addPage":
        return this.renderAddPage();
      case "checkPage":
        return this.renderCheckPage();
    }
  };
  renderListPage = () => {
    let data = this.state.data;
    let { spin, record } = this.state;
    const { getFieldDecorator } = this.props.form;

    return (
      <div className="editNotice">
        <Button type="primary" className="editNotice-add" onClick={this.onAdd}>
          添加新通知
        </Button>
        <Spin spinning={spin}>
          <Table
            className="editNotice-table"
            columns={columns({
              onEdit: record => {
                this.onEdit(record);
              }
            })}
            dataSource={data}
          />
          <Modal
            title="添加新通知"
            visible={this.state.visible}
            width={700}
            onOk={this.handleOk}
            onCancel={this.handleCancel}
            destroyOnClose={true}
            footer={[
              <Button key="back" onClick={this.handleCancel}>
                取消
              </Button>,
              <Button key="submit" type="primary" onClick={this.save}>
                保存
              </Button>,
              <Button key="submit" type="primary" onClick={this.publish}>
                发布
              </Button>
            ]}
          >
            <Form {...formItemLayout} className="editNotice-form">
              {/* <h1>消息编辑</h1> */}
              <Form.Item
                className="editNotice-form-item"
                label={<span>标题&nbsp;</span>}
              >
                {getFieldDecorator("title", {
                  rules: [
                    {
                      required: true,
                      message: "请输入标题",
                      whitespace: true
                    }
                  ]
                })(<Input />)}
              </Form.Item>
              <Form.Item
                className="editNotice-form-item"
                label={<span>消息内容&nbsp;</span>}
              >
                {getFieldDecorator("content", {
                  rules: [
                    {
                      required: true,
                      message: "请输入消息内容",
                      whitespace: true
                    }
                  ]
                })(<TextArea className="editNotice-form-item-textarea" />)}
              </Form.Item>
              {/* <div className="editNotice-form-btns">
            <Button type="primary" htmlType="submit" onClick={this.save}>
              保存
            </Button>
            <Button type="primary" htmlType="submit" onClick={this.publish}>
              发布
            </Button>
          </div> */}
            </Form>
          </Modal>
          <Modal
            title="修改通知"
            visible={this.state.editVisible}
            width={700}
            onOk={this.handleOk}
            onCancel={this.handleCancel}
            destroyOnClose={true}
            footer={[
              <Button key="back" onClick={this.handleCancel}>
                取消
              </Button>,
              <Button
                key="submit"
                type="primary"
                onClick={() => {
                  this.save(2);
                }}
              >
                保存
              </Button>
            ]}
          >
            <Form {...formItemLayout} className="editNotice-form">
              {/* <h1>消息编辑</h1> */}
              <Form.Item
                className="editNotice-form-item"
                label={<span>标题&nbsp;</span>}
              >
                {getFieldDecorator("title", {
                  rules: [
                    {
                      required: true,
                      message: "请输入标题",
                      whitespace: true
                    }
                  ],
                  initialValue: record.title
                })(<Input />)}
              </Form.Item>
              <Form.Item
                className="editNotice-form-item"
                label={<span>消息内容&nbsp;</span>}
              >
                {getFieldDecorator("content", {
                  rules: [
                    {
                      required: true,
                      message: "请输入消息内容",
                      whitespace: true
                    }
                  ],
                  initialValue: record.content
                })(<TextArea className="editNotice-form-item-textarea" />)}
              </Form.Item>
            </Form>
          </Modal>
        </Spin>
      </div>
    );
  };
  // renderCheckPage = () => {
  //   const { getFieldDecorator } = this.props.form;
  //   let record = this.state.record;
  //   return (
  //     <div className="editNotice">
  //       <Button type="primary" className="editNotice-add" onClick={this.onBack}>
  //         返回
  //       </Button>

  //     </div>
  //   );
  // };
  // renderAddPage = () => {
  //   const { getFieldDecorator } = this.props.form;
  //   return (
  //     <div className="editNotice">
  //       <Button type="primary" className="editNotice-add" onClick={this.onBack}>
  //         返回
  //       </Button>

  //     </div>
  //   );
  // };
  componentDidMount = async () => {
    await this.getData();
  };
  getData = async () => {
    let res;
    this.setState({
      spin: true
    });
    try {
      res = await http().getTable({
        resid: noticeId
      });
      // if(res.data.error === 0){
      this.setState({
        data: res.data.data
      });
      // }
    } catch (error) {}
    this.setState({
      spin: false
    });
  };
  onAdd = () => {
    this.setState({
      // page: "addPage",
      visible: true
    });
  };
  onBack = () => {
    this.setState({
      page: "listPage"
    });
  };
  onEdit = record => {
    this.setState({
      editVisible: true,
      record
    });
  };
  render() {
    const { data, record, page } = this.state;

    return this.renderContent();
  }
}
export default Form.create()(EditNotice);
