import React, { Component } from "react";
import "./Home.less";
import { Menu, Icon, Button, Tabs, Upload, Popover } from "antd";
import logo from "../../../assets/images/1556898894199.jpg";
const { SubMenu } = Menu;
const { TabPane } = Tabs;

class Home extends Component {
  state = {
    collapsed: false,
    imageUrl: "",
    userInfo: {}
  };
  toggleCollapsed = () => {
    this.setState({
      collapsed: !this.state.collapsed
    });
  };
  componentDidMount = () => {
    const userInfo = JSON.parse(localStorage.getItem("userInfo"));
    this.setState({
      userInfo
    });
  };
  getBase64 = (img, callback) => {
    const reader = new FileReader();
    reader.addEventListener("load", () => callback(reader.result));
    reader.readAsDataURL(img);
  };
  handleChange = info => {
    if (info.file.status === "uploading") {
      this.setState({ loading: true });
      return;
    }
    if (info.file.status === "done") {
      // Get this url from response in real world.
      this.getBase64(info.file.originFileObj, imageUrl =>
        this.setState({
          imageUrl,
          loading: false
        })
      );
    }
  };
  render() {
    const uploadButton = (
      <div>
        {/* <Icon type={this.state.loading ? 'loading' : 'plus'} /> */}
        <div className="ant-upload-text">请上传照片</div>
      </div>
    );
    const content = (
      <div><Icon type='logout'></Icon>退出</div>
    )
    const { imageUrl, userInfo } = this.state;
    return (
      <div className="home">
        <div className="home-left">
          <Menu
            defaultSelectedKeys={["1"]}
            defaultOpenKeys={["sub1"]}
            mode="inline"
            theme="dark"
            style={{ height: "100vh" }}
            inlineCollapsed={this.state.collapsed}
          >
            <div className="home-left-logo">
              <img className="home-left-logo-img" src={logo}></img>
            </div>
            <Menu.Item key="1">
              <Icon type="pie-chart" />
              <span>CHASE-IBD数据库</span>
            </Menu.Item>
            <Menu.Item key="2">
              <Icon type="desktop" />
              <span>数据库委员会</span>
            </Menu.Item>
          </Menu>
        </div>
        <div className="home-right">
          <div className="home-right-top">
            <div className="home-right-top-tabs">
              <Tabs
                defaultActiveKey="1"
                tabBarStyle={{
                  height: "70px",
                  display: "flex",
                  alignItems: "center",
                  paddingLeft: "50px"
                }}
              >
                <TabPane tab="通知" key="1">
                  Content of Tab Pane 3
                </TabPane>
                <TabPane tab="申请加入数据库" key="2">
                  Content of Tab Pane 1
                </TabPane>
                <TabPane tab="研究项目申请" key="3">
                  Content of Tab Pane 2
                </TabPane>
                <TabPane tab="申请流程说明" key="4">
                  Content of Tab Pane 3
                </TabPane>
                <TabPane tab="项目公示" key="5">
                  Content of Tab Pane 3
                </TabPane>
              </Tabs>
            </div>

            <Popover content={content} placement="bottom" >
            <div className="home-right-top-user">
              {/* <Upload
                name="avatar"
                listType="picture-card"
                className="avatar-uploader"
                showUploadList={false}
                action="https://www.mocky.io/v2/5cc8019d300000980a055e76"
                onChange={this.handleChange}
              >
                {imageUrl ? (
                  <img src={imageUrl} alt="avatar" style={{ width: "100%" }} />
                ) : (
                  uploadButton
                )}
              </Upload> */}
                <div>
                  <Icon type="user" style={{ fontSize: "20px" }} />
                </div>
                <div>{userInfo.Data}</div>

              {/* <div> <Icon type="logout" style={{fontSize:"20px"}} /></div> */}
            </div>
              </Popover>
          </div>
          <div className="home-right-content">
            <div></div>
          </div>
        </div>
      </div>
    );
  }
}

export default Home;
