import React, { Component } from "react";
import "./Home.less";
import { Menu, Icon, Button, Tabs, Upload, Popover, Alert,Form,Modal,Input,message } from "antd";
import logo from "../../../assets/images/logo.png";
import NoticeList from "../NoticeList";
import ApplyDataBase from "../ApplyDataBase";
import ApplyProject from "../ApplyProject";
import ActApply from "../ActApply";
import MenuDataBase from "../MenuDataBase/MenuDataBase";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import MenuCommittee from "../MenuCommittee";
import http from '../../../utils/api'
const { TabPane } = Tabs;
const doctorInfoId = '622204211916'
class Home extends Component {
  state = {
    collapsed: false,
    imageUrl: "",
    userInfo: {},
    currentItem: 1,
    visible:false,
    doctorInfo:{}
  };
  constructor(props) {
    super(props);
  }
  toggleCollapsed = () => {
    this.setState({
      collapsed: !this.state.collapsed
    });
  };
  componentDidMount = async () => {
    const userInfo = JSON.parse(localStorage.getItem("userInfo"));
    const currentItem = JSON.parse(localStorage.getItem("currentItem"));
    if (currentItem) {
      this.setState({
        userInfo,
        currentItem
      });
    } else {
      this.setState({
        userInfo
      });
    }
  };
  getBase64 = (img, callback) => {
    const reader = new FileReader();
    reader.addEventListener("load", () => callback(reader.result));
    reader.readAsDataURL(img);
  };
  handleChange = info => {
    if (info.file.status === "uploading") {
      this.setState({ loading: true });
      return;
    }
    if (info.file.status === "done") {
      // Get this url from response in real world.
      this.getBase64(info.file.originFileObj, imageUrl =>
        this.setState({
          imageUrl,
          loading: false
        })
      );
    }
  };
  //修改个人信息
  onEditInfo = async() => {
    const phoneNumber = this.state.userInfo.UserInfo.EMP_HANDPHONE;
    let res;
    try {
      res = await http().getTable({
        resid: doctorInfoId,
        cmswhere:`phoneNumber = ${phoneNumber}`
      });
      if (res.data.error === 0) {
        console.log("res",res.data)
        this.setState({
          doctorInfo: res.data.data[0]
        });
      }
    } catch (error) {
      message.error(error.message);
    }
    this.setState({
      visible:true
    })
  }
  onSave = () => {
    const { form } = this.props;
    let res;
    this.props.form.validateFieldsAndScroll(async (err, values) => {
      if (err) {
        return;
      }
      let registerData = {
        Handphone: form.getFieldValue("phone"), // 手机号
        userid: form.getFieldValue("postNumber"), //执业证号码
        occupationNumber: form.getFieldValue("postNumber"), //执业证号码
        validcode: form.getFieldValue("valid"), // 验证码
        hospital: form.getFieldValue("hospital"), // 医院
        nickname: form.getFieldValue("doctor"),
        // office:form.office,
        validresid: 616852937051
        // unionid: this.unionid,
        // openid: this.openid
      };
    })
    // this.setState({
    //   visible:false
    // })
  }
  handleCancel = () => {
    this.setState({
      visible:false
    })
  }
  //退出
  logout = () => {
    console.log("logout", this.props);
    localStorage.removeItem("userInfo");
    localStorage.removeItem("currentItem");
    this.props.history.replace({
      pathname: "/"
    });
  };
  //切换路由
  onMenuDetail = name => {
    if (name === "home") {
      localStorage.setItem("currentItem", 1);
      this.setState({
        currentItem: 1
      });
    } else {
      localStorage.setItem("currentItem", 2);
      this.setState({
        currentItem: 2
      });
    }
    this.props.history.push({
      pathname: `/${name}`
    });
  };
  render() {
    const {getFieldDecorator} = this.props.form;
    const {doctorInfo} = this.state;
    const uploadButton = (
      <div>
        <div className="ant-upload-text">请上传照片</div>
      </div>
    );
    const content = (
      <React.Fragment>
        <div style={{ margin: "10px 0 ",display:"flex",flexDirection:"column" }}>

        <Button
            type="primary"
            icon="edit"
            size="small"
            onClick={this.onEditInfo}
          >
            修改信息
          </Button>
          <Button
            type="primary"
            icon="logout"
            size="small"
            onClick={this.logout}
            style={{marginTop:"10px"}}
          >
            退出登录
          </Button>
        </div>
      </React.Fragment>
    );
    const { imageUrl, userInfo, currentItem } = this.state;
    return (
      <div className="home">

<Modal
            title="修改个人信息"
            visible={this.state.visible}
            width={600}
            onOk={this.handleOk}
            onCancel={this.handleCancel}
            destroyOnClose={true} 

            footer={[
              <Button key="back" onClick={this.handleCancel}>
                取消
              </Button>,
              <Button
                key="submit"
                type="primary"
                onClick={() => {
                  this.onSave();
                }}
              >
                保存
              </Button>
            ]}
          >
          <Form onSubmit={this.handleSubmit} className="home-modal">
              <Form.Item>
                {getFieldDecorator("doctor", {
                  rules: [{ required: true, message: "请输入您的姓名!" }],
                  initialValue: doctorInfo.doctor
                })(
                  <Input
                    prefix={
                      <Icon type="user" style={{ color: "rgba(0,0,0,.25)" }} />
                    }
                    placeholder="姓名"
                  />
                )}
              </Form.Item>
              <Form.Item>
                {getFieldDecorator("hospital", {
                  rules: [{ required: true, message: "请输入您所在的医院!" }],
                  initialValue: doctorInfo.hospital
                })(
                  <Input
                    prefix={
                      <Icon type="user" style={{ color: "rgba(0,0,0,.25)" }} />
                    }
                    placeholder="医院"
                  />
                )}
              </Form.Item>
              <Form.Item>
                {getFieldDecorator("post", {
                  rules: [{ required: true, message: "请输入您的职称!" }],
                  initialValue: doctorInfo.post
                })(
                  <Input
                    prefix={
                      <Icon type="user" style={{ color: "rgba(0,0,0,.25)" }} />
                    }
                    placeholder="职称"
                  />
                )}
              </Form.Item>
              <Form.Item>
                {getFieldDecorator("postNumber", {
                  rules: [{ required: true, message: "请输入您的执业证号码!" }],
                  initialValue: doctorInfo.occupationNumber
                })(
                  <Input
                    prefix={
                      <Icon type="user" style={{ color: "rgba(0,0,0,.25)" }} />
                    }
                    placeholder="执业证号码"
                  />
                )}
              </Form.Item>
            </Form>
            </Modal>
        <div className="home-left">
          <Menu
            selectedKeys={[`${currentItem}`]}
            defaultOpenKeys={["sub1"]}
            mode="inline"
            theme="dark"
            style={{ height: "100vh" }}
            inlineCollapsed={this.state.collapsed}
          >
            <div className="home-left-logo">
              <img className="home-left-logo-img" src={logo}></img>
            </div>
            <Menu.Item
              key="1"
              onClick={() => {
                this.onMenuDetail("home");
              }}
            >
              <Icon type="pie-chart" />
              <span>CHASE-IBD数据库</span>
            </Menu.Item>
            <Menu.Item
              key="2"
              onClick={() => {
                this.onMenuDetail("menuCommittee");
              }}
            >
              <Icon type="desktop" />
              <span>数据库委员会</span>
            </Menu.Item>

          </Menu>
        </div>
        <div className="home-right">
          <div className="home-right-top">
            <div className="home-right-top-tabs">
              <Route path="/home" component={MenuDataBase}></Route>
              <Route path="/menuCommittee" component={MenuCommittee}></Route>
            </div>

            <div className="home-right-top-user">
              {/* <Upload
                name="avatar"
                listType="picture-card"
                className="avatar-uploader"
                showUploadList={false}
                action="https://www.mocky.io/v2/5cc8019d300000980a055e76"
                onChange={this.handleChange}
              >
                {imageUrl ? (
                  <img src={imageUrl} alt="avatar" style={{ width: "100%" }} />
                ) : (
                  uploadButton
                )}
              </Upload> */}
              <Popover content={content} placement="bottom">
                <div>
                  <Icon type="user" style={{ fontSize: "20px" }} />
                </div>
                <div>{userInfo.Data}</div>
              </Popover>
            </div>
          </div>
          <div className="home-right-content">
            <div></div>
          </div>
        </div>

      </div>
    );
  }
}

export default  Form.create()(Home);
