import React, { Component } from "react";
import "./UploadFile.less";
import {
  Collapse,
  Icon,
  Input,
  Upload,
  Button,
  Form,
  message,
  Spin,
  Modal,
  Table
} from "antd";
import http from "../../../utils/api";

const fileId = '621892190597'
const { TextArea } = Input;
const columns = props => {
  const { onEdit, onPublish, data } = props;
  return [
    {
      title: "序号",
      dataIndex: "number",
      key: "number",
      width: 80
    },
    {
      title: "标题",
      dataIndex: "title",
      key: "title",
      width: 250,
      render: text => <a>{text}</a>
    },
    {
      title: "消息内容",
      dataIndex: "content",
      key: "content",
      width: 400
    },
    {
      title: "发布者",
      dataIndex: "publishPerson",
      key: "publishPerson",
      width: 150
    },
    {
      title: "发布时间",
      dataIndex: "publishTime",
      key: "publishTime",
      width: 150
    },
    {
      title: "操作",
      key: "action",
      width: 150,
      render: (text, record) => (
        <span>
          <a
            onClick={() => {
              onEdit(record);
            }}
          >
            修改
          </a>
          )}
        </span>
      )
    }
  ];
};
const uploadFile = (file, url) => {
  return new Promise((resolve, reject) => {
    let fd = new FormData();
    fd.append("file", file, file.name);
    const xhr = new XMLHttpRequest();
    xhr.open("POST", url);
    xhr.onload = () => {
      const data = JSON.parse(xhr.response);
      if (xhr.status === 200 && (data.error === 0 || data.error === "0")) {
        const imgUrl = data.data;
        resolve(imgUrl);
      } else {
        reject(data);
      }
    };
    xhr.send(fd);
  });
};
class UploadFile extends Component {
  state = {
    spin: false,
    data: [],
    record: {},
    visible: false,
    fileUrl: ""
  };
  componentDidMount() {
    // this.getApplyData();
  }

  //获取文件表记录
  getData = async () => {
    let res;
    this.setState({
      loading: true
    });
    try {
      res = await http().getTable({
        resid: fileId
      });
      if (res.data.error == 0) {
        this.setState({
          applyList: res.data.data
        });
      }
    } catch (error) {
      message.error(error.message);
    }
    this.setState({
      loading: false
    });
  };

  handleSubmit = (e, type) => {
    this.props.form.validateFieldsAndScroll((err, values) => {
      if (!err) {
        this.onSubmit(values, type);
      }
    });
  };

  //上传文件
  handleUploadFile = async fileInfo => {
    const file = fileInfo.file;
    const bucketname = "realsun";
    const type = "";
    const fileUrl = `http://ivf.realsun.me:9001/api/AliyunOss/PutOneImageObject?bucketname=${encodeURIComponent(
      bucketname
    )}&srctype=${encodeURIComponent(type)}`;
    try {
      await uploadFile(file, fileUrl).then(fileUrl => {
        this.setState({
          fileUrl
        });
      });
    } catch (err) {
      return message.error(err.message);
    }
  };
  //提交表单
  onSubmit = async (values) => {
    console.log("values",values)
    http().clearCache()
    let res;
    let obj = {};
      obj = {
        fileName: values.fileName,
        fileUrl: values.fileUrl,
      };
    try {
      res = await http().addRecords({
        resid: fileId,
        data: [obj]
      });
      if (res.data.Error == 0) {
        message.success("操作成功");
      }
      this.getData();
    } catch (error) {
      message.error(error.message);
    }
  };
  onAdd = () => {
    this.setState({
      visible: true
    });
  };
  handleCancel = () => {
    this.setState({
      visible:false
    })
  }
  getBase64 = (img, callback) => {
    const reader = new FileReader();
    reader.addEventListener("load", () => callback(reader.result));
    reader.readAsDataURL(img);
  };
  handleChange = info => {
    console.log("info", info);
    if (info.file.status === "uploading") {
      return;
    }
    if (info.file.status === "done") {
      // Get this url from response in real world.
      this.getBase64(info.file.originFileObj, imageUrl =>
        this.setState({
          imageUrl
        })
      );
    }
  };
  render() {
    const { getFieldDecorator } = this.props.form;
    const { spin, record, data, fileUrl } = this.state;
    const formItemLayout = {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 8 }
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 16 }
      }
    };
    const tailFormItemLayout = {
      wrapperCol: {
        xs: {
          span: 24,
          offset: 0
        },
        sm: {
          span: 16,
          offset: 8
        }
      }
    };
    const uploadButton = (
      <div>
        <Icon type={this.state.loading ? "loading" : "plus"} />
        <div className="ant-upload-text">Upload</div>
      </div>
    );

    return (
      <div className="editNotice">
        <Button type="primary" className="editNotice-add" onClick={this.onAdd}>
          添加新通知
        </Button>
        <Spin spinning={spin}>
          <Table
            key="3"
            className="editNotice-table"
            columns={columns({
              onEdit: record => {
                this.onEdit(record);
              },
              onPublish: (record, type) => {
                this.onPublish(record, type);
              },
              data: data
            })}
            scroll={{ x: 1000, y: "calc(100vh - 260px)" }}
            dataSource={data}
          />
          <Modal
            title="上传文件"
            visible={this.state.visible}
            width={700}
            onOk={this.handleOk}
            onCancel={this.handleCancel}
            destroyOnClose={true}
            footer={[
              <Button key="back" onClick={this.handleCancel}>
                取消
              </Button>,
              <Button key="submit" type="primary" onClick={this.onSubmit}>
                提交
              </Button>
            ]}
          >
            <Form {...formItemLayout} className="editNotice-form">
              {/* <h1>消息编辑</h1> */}
              <Form.Item
                className="editNotice-form-item"
                label={<span>资料名称&nbsp;</span>}
              >
                {getFieldDecorator("fileName", {})(<Input />)}
              </Form.Item>
              <Form.Item
                className="editNotice-form-item"
                label={<span>资料&nbsp;</span>}
              >
                {getFieldDecorator("file", {})(
                  <Upload
                    name="file"
                    showUploadList={false}
                    customRequest={this.handleUploadFile}
                    // fileList={fileList}
                    onRemove={this.handleRemoveFile}
                    onChange={this.handleChange}
                  >
                    <Button>
                      <Icon type="upload" /> 上传
                    </Button>
                  </Upload>
                )}
                <div>
                  <a href={fileUrl}>{fileUrl}</a>
                </div>
              </Form.Item>
            </Form>
          </Modal>

          <Modal
            title="修改通知"
            visible={this.state.editVisible}
            width={700}
            onOk={this.handleOk}
            onCancel={this.handleCancel}
            destroyOnClose={true}
            footer={[
              <Button key="back" onClick={this.handleCancel}>
                取消
              </Button>,
              <Button
                key="submit"
                type="primary"
                onClick={() => {
                  this.save(2);
                }}
              >
                保存
              </Button>
            ]}
          >
            <Form {...formItemLayout} className="editNotice-form">
              {/* <h1>消息编辑</h1> */}
              <Form.Item
                className="editNotice-form-item"
                label={<span>标题&nbsp;</span>}
              >
                {getFieldDecorator("title", {
                  rules: [
                    {
                      required: true,
                      message: "请输入标题",
                      whitespace: true
                    }
                  ],
                  initialValue: record.title
                })(<Input />)}
              </Form.Item>
              <Form.Item
                className="editNotice-form-item"
                label={<span>消息内容&nbsp;</span>}
              >
                {getFieldDecorator("content", {
                  rules: [
                    {
                      required: true,
                      message: "请输入消息内容",
                      whitespace: true
                    }
                  ],
                  initialValue: record.content
                })(<TextArea className="editNotice-form-item-textarea" />)}
              </Form.Item>
            </Form>
          </Modal>
        </Spin>
      </div>
    );
  }
}

export default Form.create()(UploadFile);
